mapper.select:
SELECT id, username, password_hash, email, role, phone, created_at, updated_at, last_login
FROM users
WHERE username = #{account} OR email = #{account}

mapper.update:
UPDATE users
SET last_login = #{lastLogin}, updated_at = #{lastLogin}
WHERE id = #{userId}

UPDATE users
SET last_logout_time = #{lastLogoutTime},
    updated_at = #{updatedAt}
WHERE id = #{id}
